var link=document.querySelector(".write-us"),popup=document.querySelector(".modal-content"),close=document.querySelector(".close-btn"),name=popup.querySelector("[name=name]"),form=popup.querySelector("form"),email=popup.querySelector("[name=email]");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show"),name.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(a){name.value&&email.value||(a.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(a){27===a.keyCode&&(popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error"))});